resolver = require('./tango.resolver.base');
util = require('util');
Backbone = require('backbone');

BackboneResolver = function() {	
}
util.inherits(BackboneResolver, resolver);

BackboneResolver.prototype.getUrl = function(model) {
    var obj = this.getInstance(model);
	
    if (!obj instanceof Backbone.Model)
        throw model + ' is not a Backbone.Model';

    url = obj.url();
    if (!url)
        throw 'model ' + model + ' does not have a url / urlRoot set ?';

    return url;
}

module.exports = BackboneResolver;